import React, { Component } from 'react';
import axios from 'axios';

export default class CreateUser extends Component {
    state = {
        users: [],
        username: '',
        error: '',
        successMessage: ''
    }

    async componentDidMount() {
        const res = await axios.get('http://localhost:4000/api/users');
        this.setState({ users: res.data });
        this.getUsers();
    }

    onChangeUsername = (e) => {
        this.setState({
            username: e.target.value,
            error: '',
            successMessage: ''
        });
    }

    getUsers = async () => {
        const res = await axios.get('http://localhost:4000/api/users');
        this.setState({ users: res.data });
    }

    onSubmit = async e => {
        e.preventDefault();
        const { username } = this.state;

        if (!username) {
            this.setState({ error: 'Please enter a username.' });
            return;
        }
        if (username.length < 3) {
            this.setState({ error: 'Username must be at least 3 characters long.' });
            return;
        }

        const userExists = this.state.users.some(user => user.username === username);
        if (userExists) {
            this.setState({ error: 'Username already exists.' });
            return;
        }

        try {
            await axios.post('http://localhost:4000/api/users', { username });
            this.setState({
                username: '',
                successMessage: 'User created successfully!',
                error: ''
            });
            this.getUsers();
        } catch (error) {
            this.setState({
                error: 'Failed to create user.',
                successMessage: ''
            });
        }
    }

    deleteUser = async (id) => {
        await axios.delete('http://localhost:4000/api/users/' + id);
        this.getUsers();
    }

    render() {
        return (
            <div className="row">
                <div className="col-md-4">
                    <div className="card card-body">
                        <h3>Create New User</h3>
                        <form onSubmit={this.onSubmit}>
                            <div className="form-group">
                                <input 
                                    type="text" 
                                    className="form-control" 
                                    value={this.state.username}
                                    onChange={this.onChangeUsername}
                                />
                                {this.state.error && <div style={{ color: 'red' }}>{this.state.error}</div>}
                                {this.state.successMessage && <div style={{ color: 'green' }}>{this.state.successMessage}</div>}
                            </div>
                            <button type="submit" className="btn btn-primary">
                                Save
                            </button>
                        </form>
                    </div>
                </div>
                <div className="col-md-8">
                    <ul className="list-group">
                        {this.state.users.map(user => (
                            <li className="list-group-item list-group-item-action" key={user._id}
                                onDoubleClick={() => this.deleteUser(user._id)}
                            >
                                {user.username}
                            </li>
                        ))}
                    </ul>
                </div>
            </div>
        );
    }
}
