import React, { Component } from 'react';
import axios from 'axios';

export default class CreateUser extends Component {
   state = {
        users: [],
        username: '',
        error: ''
    }

    async componentDidMount() {
        const res = await axios.get('http://localhost:4000/api/users');
        this.setState({ users: res.data });
        this.getUsers();
        console.log(this.state.users);
    }

    onChangeUsername = (e) => {
        this.setState({
            username: e.target.value,
            error: ''
        });
    }
    getUsers = async () => {
        const res = await axios.get('http://localhost:4000/api/users');
        this.setState({users: res.data});
    }
    onSubmit = async e => {
        e.preventDefault();
        const { username } = this.state;

        // Validaciones básicas
        if (!username) {
            this.setState({ error: 'Please enter a username.' });
            return;
        }
        if (username.length < 3) {
            this.setState({ error: 'Username must be at least 3 characters long.' });
            return;
        }

        // Comprobación de duplicados (opcionalmente podría ser mejor hacerlo en el backend)
        const userExists = this.state.users.some(user => user.username === username);
        if (userExists) {
            this.setState({ error: 'Username already exists.' });
            return;
        }
        await axios.post('http://localhost:4000/api/users', {
            username
        })
        this.setState({username: ''});
        this.getUsers();
    }
    deleteUser = async (id) => {
        await axios.delete('http://localhost:4000/api/users/' + id);
        this.getUsers();
    }


    render() {
        return (
            <div className="row">
                <div className="col-md-4">
                    <div className="card card-body">
                        <h3>Create New User</h3>
                        <form onSubmit={this.onSubmit}>
                            <div className="form-group">
                                <input 
                                    type="text" 
                                    className="form-control" 
                                    value={this.state.username}
                                    onChange={this.onChangeUsername}
                                />
                            </div>
                            <button type="submit" className="btn btn-primary">
                                Save
                            </button>
                        </form>
                    </div>
                </div>
                <div className="col-md-8">
                    <ul className="list-group">
                        {this.state.users.map(user => (
                            <li className="list-group-item list-group-item-action" key={user._id}
                            onDoubleClick={()=>this.deleteUser(user._id)}
                            >
                                {user.username}
                            </li>
                        ))}
                    </ul>
                </div>
            </div>
        );
    }  
}
