import React, { useState, useEffect } from 'react';
import axios from 'axios';
import DatePicker from 'react-datepicker';
import { useParams, useNavigate } from 'react-router-dom';
import 'react-datepicker/dist/react-datepicker.css';

function CreateNote() {
    const [users, setUsers] = useState([]);
    const [userSelected, setUserSelected] = useState('');
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [date, setDate] = useState(new Date());
    const [isEditing, setIsEditing] = useState(false);
    const [noteId, setNoteId] = useState('');
    const [alert, setAlert] = useState({ show: false, message: '', type: '' });

    const { id } = useParams();
    const navigate = useNavigate();

    useEffect(() => {
        async function fetchData() {
            try {
                const res = await axios.get('http://localhost:4000/api/users');
                if (res.data.length > 0) {
                    setUsers(res.data);
                    setUserSelected(res.data[0].username);
                } else {
                    setAlert({ show: true, message: 'No users available to assign the note.', type: 'danger' });
                }

                if (id) {
                    const noteResponse = await axios.get(`http://localhost:4000/api/notes/${id}`);
                    setTitle(noteResponse.data.title);
                    setContent(noteResponse.data.content);
                    setDate(new Date(noteResponse.data.date));
                    setUserSelected(noteResponse.data.author);
                    setIsEditing(true);
                    setNoteId(id);
                }
            } catch (error) {
                console.error("Failed to fetch data", error);
                setAlert({ show: true, message: 'Failed to load data!', type: 'danger' });
            }
        }
        fetchData();
    }, [id]);

    const onSubmit = async (e) => {
        e.preventDefault();
        if (!userSelected) {
            setAlert({ show: true, message: 'No user selected. Cannot save note.', type: 'danger' });
            return;
        }
        if (!title.trim() || !content.trim()) {
            setAlert({ show: true, message: 'Please fill in all fields.', type: 'danger' });
            return;
        }

        const newNote = {
            title,
            content,
            date,
            author: userSelected
        };

        try {
            if (isEditing) {
                await axios.put(`http://localhost:4000/api/notes/${noteId}`, newNote);
            } else {
                await axios.post('http://localhost:4000/api/notes', newNote);
            }
            navigate('/');
        } catch (error) {
            console.error("Failed to save the note", error);
            setAlert({ show: true, message: 'Failed to save the note!', type: 'danger' });
        }
    };

    return (
        <div className="col-md-6 offset-md-3">
            <div className="card card-body">
                <h4>Create a Note</h4>
                {alert.show && (
                    <div className={`alert alert-${alert.type}`} role="alert">
                        {alert.message}
                    </div>
                )}
                <div className="form-group">
                    <select className="form-control" value={userSelected} onChange={e => setUserSelected(e.target.value)}>
                        {users.map(user => (
                            <option key={user._id} value={user.username}>{user.username}</option>
                        ))}
                    </select>
                </div>
                <div className="form-group">
                    <input type="text" className="form-control" placeholder="Title" value={title} onChange={e => setTitle(e.target.value)} />
                </div>
                <div className="form-group">
                    <textarea className="form-control" placeholder="Content" value={content} onChange={e => setContent(e.target.value)} />
                </div>
                <div className="form-group">
                    <DatePicker className="form-control" selected={date} onChange={setDate} />
                </div>
                <button className="btn btn-primary" onClick={onSubmit} disabled={!userSelected || !title.trim() || !content.trim()}>Save</button>
            </div>
        </div>
    );
}

export default CreateNote;
